import React, { useState, useRef, useEffect } from 'react';
import axios from 'axios';
import kilter_board from '../assets/kilter_board.png'; // Ensure the path is correct
import './RouteOps.css'; // Make sure the CSS file is named correctly and imported
import { useNavigate } from 'react-router-dom';
import { useLocation } from 'react-router-dom';

const RouteOps = () => {
    const location = useLocation();
    const navigate = useNavigate();
    const { holds, response } = location.state || {};

    // Function to get unique keys from the holds objects
    const getHoldKeys = () => {
        if (holds && holds.length > 0) {
            return Object.keys(holds[0]);
        }
        return [];
    };

    const holdKeys = getHoldKeys();

    return (
        <>
        <div>
            <h1>Update Holds</h1>
            <select name="hold_id" className="hold_id">
                {holds && holds.map(hold => (
                    <option key={hold.id} value={hold.id}>
                        {`Hold ID: ${hold.id}`}
                    </option>
                ))}
            </select>
            <select name="hold_property" className="hold_property">
                {holdKeys.map((key, index) => (
                    <option key={index} value={key}>
                        {key}
                    </option>
                ))}
            </select>
            <input type="text" name="hold_value" className="hold_value" />
            <button type="submit" className="submit">Confirm</button>
        </div>

        <div>
            <h1>Update Routes</h1>
            <select name="route_id" className="route_id">
                {response && response.map(route => (
                    <option key={route.id} value={route.id}>
                        {`Route ID: ${route.id}`}
                    </option>
                ))}
            </select>
            <select name="route_property" className="route_property">
                {holdKeys.map((key, index) => (
                    <option key={index} value={key}>
                        {key}
                    </option>
                ))}
            </select>
            <input type="text" name="route_value" className="route_value" />
            <button type="submit" className="submit">Confirm</button>
        </div>
        </>
    );
}

export default RouteOps;
